import React, { useState } from "react";
import Chatbot from "react-chatbot-kit";
import 'react-chatbot-kit/build/main.css';
import "./App.css"
import config from "./chatbot/config";
import MessageParser from "./chatbot/messageParsers";
import ActionProvider from "./chatbot/actionProvider";
import { useSelector } from "react-redux";
import Final from "./chatbot/Final";


function App() {
  const page3 = useSelector((state) => state.counter.page3)

  const [enroll, setEnroll] = useState(false);

  function handleChange(){
    setEnroll(true)
  }
  return (

<>
    {enroll ? 

    (page3 ? <Final /> : <div className="App">
    <Chatbot
      config={config}
      messageParser={MessageParser}
      actionProvider={ActionProvider}
    />
  </div>)
    : 

    <div className="enroll" >
       <div className='d-flex align-items-center justify-content-center'>
                <h2 className='text-primary'>Chatbot</h2>
            </div>
      <h2 className="enroll-h2">Hello, Je suis Qubilot, votre assistant de l'université. Avec moi une phrase suffit pour trouver ce que vous cherché en tant que etudiant </h2>

      <button className="enroll-button" onClick={handleChange}>Enroll Now!</button>
      <Button type="primary" className='me-2'>Send</Button>
    </div>}
    </>
  );

}

export default App;
